sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

   if (!g_form.isNewRecord() && isLoading || newValue === '') {
      return;
   }
	var getEmailClient= new GlideAjax('GetEmailAdress_2');
	var lg= new GlideAjax('NotLog');
	debugfun=lg.addParam('sysparm_name',logFunction);
	lg.addParam('sysparm_id','info');

	getEmailClient.addParam('sysparm_name','getEmail');
	var sysid=g_form.getValue('u_requested_for');

	getEmailClient.addParam('sysparm_id',sysid);
	getEmailClient.getXML(callbackFunction);

	function callbackFunction(response){
		//console.log("helll");
		var email_user=response.responseXML.documentElement.getAttribute('answer');

		g_form.clearValue();
		g_form.setValue('requested_email',email_user);
	}





   //Type appropriate comment here, and begin script below

}
