sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {


          // Add your code here
		var rightNow=new GlideDateTime();
		var needtask = new GlideRecord('x_58872_needit_needit_task');
		needtask.addQuery('active',true);
		needtask.addQuery('state','!=','3');
		needtask.addQuery('due_date','<=',rightNow);
		needtask.addQuery('assigned_to',current.assigned_to);
		needtask.query();

		email.setSubject("hello email script");	


})(current, template, email, email_action, event);
