sysauto_script.script:
var usGR = new GlideRecord('sys_user');
usGR.query();

var users_total=0;
var userGR_list=[];
while (usGR.next()){
	userGR_list.push(usGR.getValue('name'));
	users_total++;
}
//gs.info(userGR_list);
var new_record = new GlideRecord('x_58872_needit_needit');


var tomorrow= new GlideDateTime();

var rand_int_user_index=0;
var rand_int_days=0;
for (var i=0;i<=20;i++){
	//new_record.initialize();
	new_record.newRecord();
	//rand_int_user_index= GlideSecureRandomUtil.getSecureRandomIntBound(users_total);
	rand_int_user_index= Math.floor(Math.random()*users_total);
	new_record.u_what_needed='service: '+i;

	new_record.setValue('u_requested_for',userGR_list[rand_int_user_index]);
	new_record.setValue('u_requested_for_email',new_record.u_requested_for.email);
	rand_int_days=0;
	//rand_int_days= GlideSecureRandomUtil.getSecureRandomIntBound(10);
	rand_int_days=Math.floor(Math.random()*10);
	tomorrow.addDaysLocalTime(rand_int_days);
	new_record.setValue('u_when_needed',tomorrow);//better than new_record.u_when_needed (Pass-By-Reference)
	tomorrow.addDaysLocalTime(-(rand_int_days));

	new_record.setValue('short_description','3333333332new test');
	new_record.insert();
}
